</main>

<style lang="scss">
	$red: #e41c23;
	$black: #0a0a0a;

	.logo-versus {
		position: absolute;
		top: 50%;
		left: 50%;
		z-index: 100;
		transform: translate(-50%, -50%);
	}

	.logo-thunder {
		position: absolute;
		top: 50%;
		left: 50%;
		z-index: 90;
		transform: translate(-50%, -50%);
	}

	.wheel-alpha {
		position: absolute;
		right: 50%;
		top: 50%;
		transform-origin: center center;
		animation: animateRotation 20s linear infinite;

		path {
			fill: $black;
		}
	}
	@keyframes animateRotation {
		from {
			transform: translate(-50%, -50%) rotate(0);
		}
		to {
			transform: translate(-50%, -50%) rotate(360deg);
		}
	}

	.wheel {
		position: absolute;
		right: 9%;
		top: 50%;
		/* transform: translate(50%, -50%); */
		transform-origin: center center;
		animation: animateRotation 20s linear infinite;

		path {
			fill: $red;
		}
	}

	@keyframes animateRotation {
		from {
			transform: translate(50%, -50%) rotate(0);
		}
		to {
			transform: translate(50%, -50%) rotate(360deg);
		}
	}

	.banner {
		width: 1000rem;
		position: fixed;
		bottom: 6rem;
		z-index: 102;

		&__baseline {
			width: 20rem;
			position: relative;
			float: left;
			padding-left: 8rem;
			opacity: 0.1;
			animation: baselineAnimation 4s infinite linear;
		}
	}

	@keyframes baselineAnimation {
		0% {
			transform: translateX(0);
		}

		100% {
			transform: translateX(-100%);
		}
	}

	.score {
		position: relative;
		background-color: $red;
		overflow: hidden;
		height: 100vh;
	}

	.score-team {
		position: absolute;
		top: 0;
		left: 0;
		width: 55vw;
		height: 100vh;
		background-color: $black;
		clip-path: polygon(0 0, 100% 0, 80% 100%, 0% 100%);
	}

	.score-layout {
		display: flex;
		flex-direction: column;
		height: 100%;
		position: relative;
		z-index: 101;

		&__wrapper {
			display: flex;
			flex: 1;
		}

		&__footer {
			height: 70px;
			background-color: $black;
			display: flex;
		}
	}

	.score-layout-wrapper {
		&__column {
			flex: 1;
			padding: 0 10rem;
			display: flex;
			align-items: center;

			&--team-a {
				color: white;
				padding-left: 0;
				padding-right: 28rem;
				text-align: right;
			}

			&--team-b {
				color: $black;
				padding-left: 28rem;
				padding-right: 0;
			}
		}
	}

	.biz {
		position: relative;

		&:before {
			content: "";
			width: 2rem;
			height: 100%;
			display: block;
			position: absolute;
			left: 0;
			transform: translateX(-50%) skewX(-10deg);
		}
	}

	.team-player {
		font-size: 5rem;
		font-weight: 500;
		list-style: none;
		padding: 0;
		margin: 0;
		width: 100%;

		li {
			padding-bottom: 1rem;
			opacity: 0;

			&:first-child {
				animation: playerAnimation 0.3s forwards;
			}

			&:nth-child(2) {
				animation: playerAnimation 0.3s 0.5s forwards;
			}

			&:nth-child(3) {
				animation: playerAnimation 0.3s 1s forwards;
			}

			&:nth-child(4) {
				animation: playerAnimation 0.3s 1.5s forwards;
			}
		}
	}

	@keyframes playerAnimation {
		from {
			opacity: 0;
			transform: translateY(1rem);
		}

		to {
			opacity: 1;
			transform: translateY(0);
		}
	}

	.score-footer {
		&__back {
			font-size: 2rem;
			font-weight: 400;
			display: inline-flex;
			align-items: center;
			padding: 0 3rem;
			color: $black;
			background-color: white;
		}

		&__empty {
			width: 10rem;
			background-color: $red;
			flex: 1;

			&:before {
				background-color: $red;
			}
		}

		&__test {
			flex: 1;

			&:before {
				background-color: $black;
			}
		}

		&__retry {
			font-size: 2rem;
			font-weight: 400;
			display: inline-flex;
			align-items: center;
			padding: 0 3rem;
			color: $red;

			&:before {
				background-color: $black;
			}
		}
	}
</style>
